import React, { useState, useLayoutEffect, useRef, useCallback, useEffect } from 'react';
import PinPopup from '@/components/PinPopup';
import useSiteStore from '@/store/useSiteStore';


// TODO: double click for pin drops
// pin popup to show in middle of innerwindow, rather than centre of pdf/perhaps could be offset to pin location
// check that pointer is held down for full 0.5s i.e. one state change on pointer down, one on pointer up; if pointer up before 0.5s, then no highlight
const elementConfig = {
  "pin": "blue",
};

type Point = {
  id: string;
  x: number;
  y: number;
  images: Image[];
  comment: string;
};

type Image = {
  key: string; // Key to retrieve the image from local storage
  pointIndex: number;
};

// Replace the PNG image with an inline SVG
// const pinSvg = `
// <svg width="20" height="20" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
//   <circle cx="12" cy="12" r="10" fill="blue"/>
// </svg>`;
const pinSvg = `
<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="800" zoomAndPan="magnify" viewBox="0 0 600 809.999993" height="1080" preserveAspectRatio="xMidYMid meet" version="1.0"><defs><clipPath id="70a86956aa"><path d="M 23.238281 0 L 576 0 L 576 810 L 23.238281 810 Z M 23.238281 0 " clip-rule="nonzero"/></clipPath><clipPath id="b8189e4e58"><path d="M 299.855469 0.578125 C 147.359375 0.578125 23.730469 124.207031 23.730469 276.703125 C 23.730469 383.59375 84.535156 476.246094 173.339844 522.136719 L 173.339844 683.386719 L 299.855469 809.902344 L 426.375 683.386719 L 426.375 522.136719 C 515.273438 476.246094 575.980469 383.59375 575.980469 276.703125 C 575.980469 124.207031 452.351562 0.578125 299.855469 0.578125 Z M 299.855469 0.578125 " clip-rule="nonzero"/></clipPath><clipPath id="6cbf055b9f"><path d="M 81 58 L 519 58 L 519 495 L 81 495 Z M 81 58 " clip-rule="nonzero"/></clipPath><clipPath id="6163491da2"><path d="M 518.445312 276.703125 C 518.445312 397.449219 420.601562 495.292969 299.855469 495.292969 C 179.109375 495.292969 81.265625 397.449219 81.265625 276.703125 C 81.265625 155.957031 179.109375 58.113281 299.855469 58.113281 C 420.601562 58.113281 518.445312 155.957031 518.445312 276.703125 Z M 518.445312 276.703125 " clip-rule="nonzero"/></clipPath><image x="0" y="0" width="1438" xlink:href="data:image/jpeg;base64," id="e8f0d84919" height="560" preserveAspectRatio="xMidYMid meet"/><clipPath id="a668bced1a"><path d="M 61.765625 37.230469 L 538.234375 37.230469 L 538.234375 513.699219 L 61.765625 513.699219 Z M 61.765625 37.230469 " clip-rule="nonzero"/></clipPath><clipPath id="ec95d1803a"><path d="M 300 37.230469 C 168.425781 37.230469 61.765625 143.890625 61.765625 275.464844 C 61.765625 407.039062 168.425781 513.699219 300 513.699219 C 431.574219 513.699219 538.234375 407.039062 538.234375 275.464844 C 538.234375 143.890625 431.574219 37.230469 300 37.230469 Z M 300 37.230469 " clip-rule="nonzero"/></clipPath></defs><rect x="-60" width="720" fill="#ffffff" y="-80.999999" height="971.999992" fill-opacity="1"/><rect x="-60" width="720" fill="#ffffff" y="-80.999999" height="971.999992" fill-opacity="1"/><g clip-path="url(#70a86956aa)"><g clip-path="url(#b8189e4e58)"><path fill="#b7dbf2" d="M 13.628906 0 L 586.371094 0 L 586.371094 810.105469 L 13.628906 810.105469 Z M 13.628906 0 " fill-opacity="1" fill-rule="nonzero"/></g></g><g clip-path="url(#6cbf055b9f)"><g clip-path="url(#6163491da2)"><g transform="matrix(0.780545, 0, 0, 0.780796, -261.455463, 58.111433)"><image x="0" y="0" width="1438" xlink:href="data:image/jpeg;base64," height="560" preserveAspectRatio="xMidYMid meet"/></g></g></g><g clip-path="url(#a668bced1a)"><g clip-path="url(#ec95d1803a)"><path fill="#042d62" d="M 61.765625 37.230469 L 538.234375 37.230469 L 538.234375 513.699219 L 61.765625 513.699219 Z M 61.765625 37.230469 " fill-opacity="1" fill-rule="nonzero"/></g></g>
</svg>
`

// @ts-ignore
function CanvasComponent({pdfId, zoomLevel, onZoomChange}) {
  const [selectedPoint, setSelectedPoint] = useState(null);  // For showing selected pin in popup
  const [showPinPopup, setShowPinPopup] = useState(false);  // Controls the visibility of the popup
  const dimensions = useSiteStore((state) => state.canvasDimensions);  // Get canvas dimensions from the store
  const canvasRef = useRef(null);

  // Store states (for pins)
  const canvasDimensions = useSiteStore((state) => state.canvasDimensions);
  const plans = useSiteStore((state) => state.plans);  // Get plans (including points) from the store
  const addPoint = useSiteStore((state) => state.addPoint);  // To add a new pin to the store
  const changePointLocation = useSiteStore((state) => state.changePointLocation);  // To change the location of a pin
  const currentPlan = plans.find((plan) => plan.id === pdfId);  // Find the current plan by its ID
  const points = currentPlan?.points || [];  // Get points from the plan
  const pdfLoaded = useSiteStore((state) => state.pdfLoaded);  // Check if the PDF is loaded
  const [startHoldTime, setStartHoldTime] = useState<number|null>(null);
  const [pointerIsUp, setPointerIsUp] = useState<boolean>(true);
  const [pointerDownLocation, setPointerDownLocation] = useState<{x: number, y: number}>({x: 0, y: 0});
  const [movablePoint, setMovablePoint] = useState<Point | null>(null);
  const [pinImage, setPinImage] = useState<HTMLImageElement | null>(null);

  useEffect(() => {
    const img = new Image();
    img.src = 'data:image/svg+xml;base64,' + btoa(pinSvg);
    img.onload = () => setPinImage(img);
  }, []);

  const renderPoints = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    // @ts-ignore
    const context = canvas.getContext('2d');
    if (!context) return;

    // Clear the canvas before rendering points
    // @ts-ignore
    context.clearRect(0, 0, canvas.width, canvas.height);

    points.forEach((point, index) => {
      // Draw the pin image (centered on point)
      // Draw the pin
      // context.fillStyle = elementConfig["pin"];
      // context.fillRect(point.x - 5, point.y - 5, 10, 10);
      const dimensionMultiplier = 30;
      const pinWidth = dimensionMultiplier * 800/1080; // Adjust size as needed
      const pinHeight = dimensionMultiplier;
      context.drawImage(
        pinImage, 
        point.x - pinWidth/2, 
        point.y- pinHeight,//2, 
        pinWidth, 
        pinHeight
      );
      
      // Draw the number
      context.fillStyle = 'white';
      context.font = '12px Arial';
      context.textAlign = 'center';
      context.textBaseline = 'middle';
      context.fillText((index + 1).toString(), point.x, point.y-pinHeight*13/20);
    });
  }, [points, pinImage]);
  // Handle pointer down to add or select a pin
  // @ts-ignore
  const handleDoublePointerDown = (event) => { // needs to be double click
    const canvas = canvasRef.current;
    if (!canvas || !currentPlan) return;
    // @ts-ignore
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    const pointer = { x, y };

    // Check if the pointer is close to any existing point (within a certain threshold)
    const closestPoint = findClosestPin(pointer);
    if (closestPoint) {
      setSelectedPoint(closestPoint);
      setShowPinPopup(true);  // Show the popup if a point is clicked
    } else {
      // If no close pin is found, add a new pin
      const pointId = Date.now().toString();
      const newPoint = { id: pointId, x, y, images: [], comment: '' };
      addPoint(currentPlan.id, newPoint);  // Add the new point to the store
      renderPoints();  // Re-render points after adding a new one
    }
  };

 // @ts-ignore
 const handlePointerDown = (event) => { // needs to be hold for 0.5s, highlight, then move
  setPointerIsUp(false);
  // if pointer up after 0.5s or x,y changed -> won't highlight pin
  // if not double click!!!
  const canvas = canvasRef.current;
  if (!canvas || !currentPlan) return;
  // @ts-ignore
  const rect = canvas.getBoundingClientRect();
  const x = event.clientX - rect.left;
  const y = event.clientY - rect.top;

  const pointer = { x, y };

  // Check if the pointer is close to any existing point (within a certain threshold)
  const closestPoint = findClosestPin(pointer);
  if (closestPoint) {
    // add closestPoint to state for drag
    // start timer, then highlight point
    setStartHoldTime(Date.now());
    setPointerDownLocation({x, y});
    // then move to pointer up location
    
    setSelectedPoint(closestPoint);

  } else {
    setStartHoldTime(null);
    setSelectedPoint(null);
    // // If no close pin is found, add a new pin
    // const pointId = Date.now().toString();
    // const newPoint = { id: pointId, x, y, images: [], comment: '' };
    // addPoint(currentPlan.id, newPoint);  // Add the new point to the store
    // renderPoints();  // Re-render points after adding a new one
  }
};
// const timeoutId = setTimeout(() => {
//   // Code to be executed after the timeout
// }, 3000); // 3000 milliseconds = 3 seconds
// @ts-ignore
const handlePointerHeldDown = (event) => {
  if (startHoldTime) {
    const timeoutId = setTimeout(() => {
      if (pointerIsUp) return;

      const canvas = canvasRef.current;
      if (!canvas || !currentPlan) return;
      // @ts-ignore
      const rect = canvas.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
    
      const pointer = { x, y };
      // check pointer is close to previous pointerdown location
      let pointerDownLocationX = pointerDownLocation.x;
      let pointerDownLocationY = pointerDownLocation.y;
      // should be +5 or -5 pixels

      if (pointer.x -15 < pointerDownLocationX && pointer.x +15 > pointerDownLocationX  && pointer.y -15 < pointerDownLocationY && pointer.y +15 > pointerDownLocationY) {
        // see if pointer is up
        
        // see if pointer is same location as before
        // if so, highlight pin - set pin to selected/moving
        setMovablePoint(selectedPoint);
        // then move point to pointer up location
        // future - add point whilst moving

      }
   }, 800); // 3000 milliseconds = 3 seconds
   timeoutId
    // action time delay of 0.5s
  
  } 
}
// for drag to move pin
// @ts-ignore
const handlePointerUp = (event) => {

  setPointerIsUp(true);
  if (movablePoint) {
    const canvas = canvasRef.current;
    if (!canvas || !currentPlan) return;
    // @ts-ignore
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
  
    const pointer = { x, y };
    // changePointLocation(currentPlan.id, movablePoint.id, x, y);
  }
}


  // Helper function to calculate the distance between two points
//@ts-ignore
  const calcDistance = (p1, p2) => {
    const a = p1.x - p2.x;
    const b = p1.y - p2.y;
    return Math.sqrt(a * a + b * b);
  };

  // Find the closest pin to the pointer
//@ts-ignore
const findClosestPin = useCallback(
    (pointer: { x: number; y: number }) => {
    // @ts-ignore
      let closestPoint: Point | null = null;
      let closestDistance: number | null = null;

      points.forEach((currentP) => {
        const currentDistance = calcDistance(pointer, currentP);
        if (currentDistance < 15 && (closestDistance === null || currentDistance < closestDistance)) {
          closestDistance = currentDistance;
          closestPoint = currentP;
        }
      });

      return closestPoint;
    },
    [points]
  );

  // UseLayoutEffect to render points when the component is mounted or points change
  // should be after pdf is loaded
  useLayoutEffect(() => {
    if (pdfLoaded) {

      renderPoints();
    }
  }, [renderPoints, pdfLoaded]);

  return (
    <>
      {/* Canvas for rendering pins */}
      <div style={{ 
        position: 'relative', 
        width: '100%', 
        height: '100%', 
        zIndex: 100,
        touchAction: movablePoint ? 'none' : 'auto'
      }}>
        <input
          type="range"
          min="0.5"
          max="3"
          step="0.1"
          value={zoomLevel}
          onChange={(e) => onZoomChange(parseFloat(e.target.value))}
          style={{
            position: 'absolute',
            bottom: 20,
            right: 20,
            width: 150,
            zIndex: 1000
          }}
        />
        <canvas
          ref={canvasRef}
          // @ts-ignore
          width={canvasDimensions.width}
          // @ts-ignore
          height={canvasDimensions.height}
          className="border border-black rounded-md bg-transparent inset-0 absolute z-10"
          style={{ 
            touchAction: movablePoint ? 'none' : 'auto',
            transform: `scale(${zoomLevel})`,
            transformOrigin: 'center'
          }}
          // @ts-ignore
          onDoubleClick={handleDoublePointerDown}
          onPointerDown={handlePointerDown}
          onPointerLeave={handlePointerUp}
          onPointerUp={handlePointerUp}
          onPointerMove={handlePointerHeldDown}
          // onDrag={handlePointerDown}
          // onDragEnd={handlePointerUp}
        />
        
        {/* Pin Popup for the selected pin - should show in middle of viewport/innerwindow*/}
        {showPinPopup && selectedPoint && (
          <PinPopup
            setShowPinPopup={setShowPinPopup}
            selectedPoint={selectedPoint}  // Pass the selected pin to the popup
            planId={currentPlan?.id || ''}  // Pass the current plan ID to the popup
          />
        )}
      </div>
    </>
  );
}

export default CanvasComponent;
